import React, { useState } from 'react';

import Card from './Componets/UI/Card';
import NewTask from './Componets/NewTask';
import TaskList from './Componets/TaskList';
import TaskHeader from './Componets/TaskHeader/TaskHeader';

import './App.css';


function App() {
  const [toDoList, setToDoList] = useState([{taskTitle:"Learn React", taskDaysLeft:"13"}]);

  const newTaskHandler = (taskObj) => {
    setToDoList( prev => [taskObj, ...toDoList]);
  }

  return (
    <div>
      <Card className="heading-card">
        <TaskHeader />
      </Card>
      <Card className="new-task-card">
        <NewTask onSaveNewTask={newTaskHandler} />
      </Card>
      <Card className="task-list-card">
        <TaskList  allTasks={toDoList} />
      </Card>
    </div>
  );
}

export default App;
