import { useState } from 'react';
import './TaskItem.css'

const TaskItem = () => {
    const [isSelected, setSelected] = useState('false');

    const taskItemHandler = (event) => {
        event.stopPropagation();
        setSelected(isSelected^true);
        console.log(isSelected);
    }
    const editHandler = (event) => {
        event.stopPropagation();
    }
    return (
        <div className={`task-item-container ${(isSelected===true ? "task-item-container-selected" : "")}`} onClick={taskItemHandler}>
            <div className='task-data-container'>
                <div className='task-title'>
                    Learn React
                </div>
                <div className='task-btn-container'>
                    <button className={"task-item-btn"} type="button" onClick={editHandler}>Edit</button>
                    <div className='task-item-btn' type="button" >Days left</div>
                </div>
            </div>
        </div>
    );
}

export default TaskItem;
