import { useState } from 'react';
import './TaskItem.css'

const TaskItem = (props) => {
    const [isSelected, setSelected] = useState(false);
    const [isEdit, setIsEdit] = useState(false);

    const taskItemHandler = (event) => {
        event.stopPropagation();
        setSelected(!isSelected);
    }
    const editHandler = (event) => {
        event.stopPropagation();
        setIsEdit(prev => !isEdit);
    }
    const titleHandler = (event) => {
        event.stopPropagation();

    }
    const titleClickHandler = (event) => {
        event.stopPropagation();

    }

    return (
        <div className={`task-item-container ${isSelected && "task-item-container-selected"}`} onClick={taskItemHandler}>
            <div className='task-item-checkbox'>
                <i className="fas fa-check-circle checkbox" style={isSelected ? {} : {visibility:'hidden'}} ></i>
            </div>
            <div className='task-data-container'>
                <div className='task-data-container__edit'>
                    {
                        !isEdit && <div className='task-title' style={!isSelected ? {} : {textDecoration:'underline'}} >{props.title}</div>
                    }
                    {
                        isEdit && <input className='task-title task-title-input' type="text" placeholder={props.title} onChange={titleHandler} onClick={titleClickHandler} />
                    }
                </div>
                <div className='task-btn-container'>
                    <div className='edit-task-title' onClick={editHandler}>
                        {
                            !isEdit && <i className="fas fa-edit"></i>
                        }
                        {
                            isEdit && <i class="fas fa-save"></i> 
                        }
                    </div>
                    <div className='task-item-days-left' type="button" >{props.daysLeft + " Days left"}</div>
                </div>
            </div>
        </div>
    );
}

export default TaskItem;
