import React, { useState } from 'react';

import Card from './Componets/UI/Card';
import NewTask from './Componets/NewTask';
import TaskList from './Componets/TaskList';
import TaskHeader from './Componets/TaskHeader/TaskHeader';

import './App.css';


function App() {
  const [toDoList, setToDoList] = useState([{taskTitle:"Learn React", taskDaysLeft:"13"}]);

  const newTaskHandler = (taskObj) => {
    setToDoList( prev => [taskObj, ...prev]);
  }
  const selectedHandler = (taskTitle) => {
    for(let obj of toDoList){
      if(obj.taskTitle === taskTitle){
        obj.isMarked ^= true;
      }
    }
    setToDoList(toDoList)
    console.log(toDoList[0].isMarked);
  }

  return (
    <div>
      <Card className="heading-card">
        <TaskHeader />
      </Card>
      <Card className="new-task-card">
        <NewTask onSaveNewTask={newTaskHandler} allTasks={toDoList} />
      </Card>
      <Card className="task-list-card">
        <TaskList onSaveSelected={selectedHandler} allTasks={toDoList} />
      </Card>
    </div>
  );
}

export default App;
