import React, { useState } from "react";

const TaskForm = (props) => {
    const [taskName, setTaskName] = useState('');
    const [taskDate, setTaskDate] = useState(new Date());

    const formHandler = (event) => {
        event.preventDefault();
        const date2 = new Date();
        const Difference_In_Time = (new Date(taskDate)).getTime() - (new Date(date2.toLocaleDateString()).getTime());
        const Difference_In_Days = Difference_In_Time / (1000 * 3600 * 24);
        
        const taskObj = {taskTitle: taskName, taskDaysLeft: parseInt(Difference_In_Days)};
        props.onSaveNewTask(taskObj);
    }

    const taskNameChangeHandler = (event) => {
        setTaskName(event.target.value);
    }
    const taskDateChangeHandler = (event) => {
        setTaskDate(event.target.value)
    }

    return (
        <div className='form-container'>
            <form onSubmit={formHandler}>   
                <div className='form-input__control'>
                    <div>
                        <label forhtml="taskname">Task name</label>
                        <input id="taskname" type="text" value={taskName} onChange={taskNameChangeHandler}></input>
                    </div>
                    <div>
                        <label forhtml="taskdate">Task date</label>
                        <input id="taskdate" type="date" value={taskDate} onChange={taskDateChangeHandler}></input>
                    </div>
                </div>
                <div className='btn'>
                    <button className='btn-tag' type="submit"><h1>Add task</h1></button>
                </div>
            </form>
        </div>
    );
}

export default TaskForm;
