import { useState } from 'react';
import './TaskHeader.css';

const TaskHeader = (props) => {
    const deleteHandler = (event) => {
        const toDoList = props.allTasks.filter( e => e.isMarker === false );
        props.onDeletedMarked(toDoList);
    }

    return (
        <div className='header-container'>
            <h1 className='header-title'>TO - DO LIST</h1>
            <div className='header-button-container'>
                <button className='header-button' type='button'>Add New Task</button>
                <button className='header-button' type='button' onClick={deleteHandler} >Delete Selected</button>
            </div>
        </div>
    );
}

export default TaskHeader;
